import React, { useState } from "react";
import { useQuery, gql, useLazyQuery } from "@apollo/client";

function DisplayUsers() {

    const QUERY_ALL_USERS = gql `
        query GetAllUsers {
            users {
                name
                id
                username
                age
            }
        }
    `

    const {data: userData, loading: userDataLoading, error: userDataError} = useQuery(QUERY_ALL_USERS);

    if (userDataLoading) {
        return <h1>Data is Loading...</h1>
    }

    if (userData) {
        console.log(userData)
    }

    if (userDataError) {
        console.log(userDataError)
    }

    return <div>
        {userData && 
            userData.users.map((user) => {
                return <div>
                    <h1>{user.name}</h1>
                    <h2>Age: {user.age}</h2>
                    <h2>@{user.username}</h2>
                    </div>
            })
        }

        </div>
}

function DisplayMovies() {

    const QUERY_ALL_MOVIES = gql `
        query GetAllUsers {
            movies {
                name
                release
            }
        }
    `

    const {data: movieData} = useQuery(QUERY_ALL_MOVIES);

    return <div>
        {movieData &&
            movieData.movies.map((movie) => {
                return <div>
                    <h1>{movie.name}</h1> <h2>was released in {movie.release}</h2>
                    </div>
            })
        }
        </div>
}

function SearchMovie() {

    const QUERY_MOVIE = gql `
        query Movie($name: String!) {
        movie(name: $name) {
            name
            release
            isInTheaters
            }
        }
    `;

    const [movieSearched, setMovieSearched] = useState("");
    const [
        fetchMovie, 
        {data: movieSearchedData, error: MovieError}
    ] = useLazyQuery(QUERY_MOVIE);

    if (MovieError) {
        console.log(MovieError)
    }

    return <div>
        <div>
            <input type='text' placeholder="Name of Movie" onChange={(event) => {
                setMovieSearched(event.target.value)
                }}/>
            <button onClick={() => {
                fetchMovie({variables: {
                    name: movieSearched,
                }})
            }}>Fetch Data</button>
            <div>{movieSearchedData && (
                <div> 
                    {" "}<h3> Movie Name: {movieSearchedData.movie.name}</h3>{" "}
                    {" "}<h3> Movie release: {movieSearchedData.movie.release}</h3>{" "}
                </div>
                )}
                {MovieError && <h3>Movie not found.</h3>}
            </div>

        </div>

        </div>
}

function DisplayData() {

    return 
        <div>
            <div>
                <input type='text' placeholder='Name'></input>
                <input type='number' placeholder='Age'></input>
                <input type='text' placeholder='Username'></input>
                <input type='text' placeholder='Nationality'></input>

                <button> Create User </button>

            </div>

        </div>
}

export {DisplayData, DisplayMovies, DisplayUsers, SearchMovie}
