import React, { useState } from "react";
import { useQuery, gql, useLazyQuery } from "@apollo/client";

const QUERY_ALL_USERS = gql `
    query GetAllUsers {
        users {
            name
            id
            username
            age
        }
    }
`

const QUERY_ALL_MOVIES = gql `
    query GetAllUsers {
        movies {
            name
            release
        }
    }
`

const QUERY_MOVIE = gql `
query getMovie($name: String!) {
       movie(name: $name) {
        name
        release
        isInTheaters
        }
    }
`


function DisplayData() {

    const {data: userData, loading: userDataLoading, error: userDataError} = useQuery(QUERY_ALL_USERS);
    const {data: movieData} = useQuery(QUERY_ALL_MOVIES);
    const [movieSearched, setMovieSearched] = useState("");
    const [
        fetchMovie, 
        {data: movieSearchedData, error: MovieError}
    ] = useLazyQuery();

    if (userDataLoading) {
        return <h1>Data is Loading...</h1>
    }

    if (userData) {
        console.log(userData)
    }

    if (userDataError) {
        console.log(userDataError)
    }

    return <div>
        {userData && 
            userData.users.map((user) => {
                return <div>
                    <h1>{user.name}</h1>
                    <h2>Age: {user.age}</h2>
                    <h2>@{user.username}</h2>
                    </div>
            })
        }

        {movieData &&
            movieData.movies.map((movie) => {
                return <div>
                    <h1>{movie.name}</h1> <h2>was released in {movie.release}</h2>
                    </div>
            })
        }

        <div>
            <input type='text' placeholder="Name of Movie" onChange={(event) => {
                setMovieSearched(event.target.value)
                }}/>
            <button>Fetch Data</button>
            {/* <div>{movieSearchedData && 
